export interface Category{
    name:string,
    posts:{
        nodes:{
            slug:string
            title:string
            date:string
            categories:{
                nodes:{
                    name:string
                    slug:string
                }[]
            }
            featuredImage:{
                node:{
                    link:string
                }
            }
        }[]
    }
}
export interface Posts{
    edges:{
        node:{
            slug:string
            title:string
            date:string
            categories:{
                nodes:{
                    name:string
                    slug:string
                }[]
            }
            featuredImage:{
                node:{
                    link:string
                }
            }
        }
    }[]
}
export interface Views{
    views:{
        slug:string
        view:number
    }[]
}
